import { useRouter } from "next/router";
import { useEffect } from "react";
import { connect } from "react-redux";
import { Button } from "antd";

import decodeToken from "../utils/auth";
import { addingCurrentUser } from "../redux/user/user-actions";

const Index = props => {
  const router = useRouter();
  useEffect(() => {
    const { token, user, addUser } = props;
    if (token) {
      const decoded = decodeToken(props.token);
      const { _id } = decoded;
      const userData = {
        id: _id
      };
      addUser(userData);
    } else if (user) {
      console.log(user);
    } else {
      router.push("/signin");
    }
  }, []);

  return (
    <div id="wrapper">
      <p>Hello Next.js</p>
      <Button type="danger">Primary</Button>
      <p>Secret: {process.env.JWT_SECRET}</p>

      <style jsx>{`
        #wrapper {
          margin: 10px;
        }
      `}</style>
    </div>
  );
};

Index.getInitialProps = async function(context) {
  const token = context.query.at;
  return {
    token
  };
};

const mapStateToProps = state => ({
  user: state.user.currentUser
});

const mapDispatchToProps = dispatch => ({
  addUser: item => dispatch(addingCurrentUser(item))
});

export default connect(mapStateToProps, mapDispatchToProps)(Index);
