import { useState, useEffect } from "react";
import { Modal, Button, Select, Row, Col } from "antd";

import Table from "../components/table";
import { performanceColumns } from "../utils/table";

function Performance() {
  const [state, setState] = useState({
    modalVisible: false
  });

  const setModalVisible = modalVisible => {
    setState({
      ...state,
      modalVisible
    });
  };

  const onSelectionChange = (selectedRowKeys, selectedRows) => {
    console.log("rows", selectedRows);
    const selectedInterest = selectedRows.map(interest => {
      return interest.name;
    });
    setState({
      ...state,
      selectedRowKeys,
      selectedInterest,
      selectedRows
    });
  };

  const { Option } = Select;
  return (
    <div id="performance">
      <Row gutter={16} style={{ width: "100%" }}>
        <Col span={4}>
          <div className="selection">
            <div className="button item">
              <Button onClick={setModalVisible} block>
                Select Ad account
              </Button>
            </div>
            <div className="select item">
              <Select style={{ width: "100%" }} placeholder="Select campaign">
                <Option value="jack">Jack</Option>
                <Option value="lucy">Lucy</Option>
                <Option value="disabled" disabled>
                  Disabled
                </Option>
                <Option value="Yiminghe">yiminghe</Option>
              </Select>
            </div>
            <div className="select item">
              <Select style={{ width: "100%" }} placeholder="Select adset">
                <Option value="jack">Jack</Option>
                <Option value="lucy">Lucy</Option>
                <Option value="disabled" disabled>
                  Disabled
                </Option>
                <Option value="Yiminghe">yiminghe</Option>
              </Select>
            </div>
            <div className="select item">
              <Select style={{ width: "100%" }} placeholder="Select ad">
                <Option value="jack">Jack</Option>
                <Option value="lucy">Lucy</Option>
                <Option value="disabled" disabled>
                  Disabled
                </Option>
                <Option value="Yiminghe">yiminghe</Option>
              </Select>
            </div>
          </div>
        </Col>
        <Col span={20}>
          <div className="stats">
            <div>Graph</div>
            <div>
              <Table
                columns={performanceColumns}
                onSelection={onSelectionChange}
                selectedRowKeys={selectedRowKeys}
                className="table"
              />
            </div>
          </div>
        </Col>
      </Row>
      <Modal
        title="Vertically centered modal dialog"
        centered
        visible={state.modalVisible}
        onOk={() => setModalVisible(false)}
        onCancel={() => setModalVisible(false)}
      >
        <p>some contents...</p>
        <p>some contents...</p>
        <p>some contents...</p>
      </Modal>
      <style jsx>
        {`
          #performance {
            display: flex;
            position: relative;
            margin-top: 70px;
            width: 100%;
          }

          .selection {
            display: flex;
            flex-direction: column;
            border: 1px solid #ccc;
            border-radius: 4px;
            height: auto;
            padding: 10px;
          }

          .item {
            margin-bottom: 30px;
            width: 100%;
          }
          .stats {
            display: flex;
            flex-direction: column;
          }
        `}
      </style>
    </div>
  );
}

export default Performance;
