import { Row, Col } from 'antd';

const DashboardLayout = props => {
 return (
     <div className="layout">
         <Row>
            <Col style={{position: 'relative', height: '100vh'}} span={4}>{props.sidebar}</Col>
            <Col style={{padding: '0 20px 0 20px', minHeight: '100vh'}} span={20}>
                {props.children}
            <footer>
                <div className="footer">
                    <p className="footer-item">&copy; 2010 - {new Date().getFullYear()}, Getch.</p>
                </div>
            </footer>
            </Col>
         </Row>

        <style jsx>{`
            .layout {
                position: relative;
                min-height: 100vh;
            }

            .layout-body {
                position: relative;
            }

            footer {
                margin-top: 100px;
                display: flex;
                justify-content: center
                width: 100%
            }

            .footer {
                position: fixed;
                // left: 0;
                bottom: 0;
                // width: 100%;
                background-color: red;
                color: white;
                text-align: center;
                margin-top: 50px;
              }
        `}</style>
     </div>
 )
}

export default DashboardLayout
