import UserActionTypes from "./user-types";

const INITIAL_STATE = {
  currentUser: null,
  fbToken: {
    token: null,
    expires: null,
  },
  signedIn: false,
  logout: false,
  error: null,
};

const userReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case UserActionTypes.SET_CURRENT_USER:
      return {
        ...state,
        currentUser: action.payload,
        error: null,
        signedIn: true,
        logout: false,
      };
    case UserActionTypes.SIGN_IN_SUCCESS:
      return {
        ...state,
        signedIn: action.payload,
        error: null,
      };
    case UserActionTypes.LOADING:
      return {
        ...state,
        loading: action.payload,
        error: null,
      };
    case UserActionTypes.AUTH_FB_SUCCESS:
      return {
        ...state,
        fbToken: { ...action.payload },
        currentUser: { ...state.currentUser, name: action.payload.name },
        error: null,
      };
    case UserActionTypes.LOG_OUT_SUCCESS:
      return {
        ...state,
        currentUser: null,
        fbToken: {
          token: null,
          expires: null,
        },
        signedIn: false,
        logout: true,
        error: null,
      };
    case UserActionTypes.PAYMENT_SUCCESSFUL:
      return {
        ...state,
        currentUser: {
          ...state.currentUser,
          plan: action.payload,
        },
        error: null,
      };
    case UserActionTypes.SIGN_IN_FAILURE:
    case UserActionTypes.AUTH_FB_FAILURE:
    case UserActionTypes.SIGN_UP_FAILURE:
    case UserActionTypes.LOG_OUT_FAILURE:
    case UserActionTypes.PAYMENT_FAILED:
      return {
        ...state,
        error: action.payload,
        loading: false,
      };
    default:
      return state;
  }
};

export default userReducer;
