import { useRouter } from "next/router";
import { useEffect } from "react";
import { connect } from "react-redux";
import { Button } from "antd";
import FacebookLogin from 'react-facebook-login';

import {getProducts, fbAuthentication} from '../api/api'

import decodeToken from "../utils/auth";
import { addingCurrentUser } from "../redux/user/user-actions";

const Index = props => {
  const router = useRouter();
  useEffect(() => {
    const { token, user, addUser } = props;
    if (token) {
      const decoded = decodeToken(props.token);
      const { _id, name, email, domain } = decoded;
      const userData = {
        id: _id,
        name, 
        email, 
        domain
      };
      addUser(userData);
    } else if (user) {
      console.log(user);
    } else {
      router.push("/signin");
    }
  }, []);

  const faceAuthStart = () => {
    console.log('#### FB auth start')
  }

  const responseFacebook = async (res) => {
    const {accessToken} = res.data
    await fbAuthentication(accessToken)
  }


  return (
    <div id="wrapper">
      <p>Hello Next.js</p>
      <FacebookLogin
        appId={process.env.FB_APP_ID}
        autoLoad={true}
        fields="name,email,id"
        onClick={faceAuthStart}
        scope="ads_management, email"
        callback={responseFacebook} />
      <Button onClick={() => getProducts('hornymonkey')} type="danger">Primary</Button>
      <p>Secret: {process.env.FB_APP_SECRET}</p>

      <style jsx>{`
        #wrapper {
          margin: 10px;
        }
      `}</style>
    </div>
  );
};

Index.getInitialProps = async function(context) {
  const token = context.query.at;
  return {
    token
  };
};

const mapStateToProps = state => ({
  user: state.user.currentUser
});

const mapDispatchToProps = dispatch => ({
  addUser: item => dispatch(addingCurrentUser(item))
});

export default connect(mapStateToProps, mapDispatchToProps)(Index);
