import { useState, useEffect } from "react";
import { connect } from "react-redux";
import { Modal, Button, Select, Row, Col } from "antd";

import { chartData } from "../utils/table";

import Table from "../components/table";
import AdAccount from '../components/adAccount'
import { performanceColumns } from "../utils/table";
import {getAdAccounts} from '../api/api'

function Performance(props) {
  const [state, setState] = useState({
    modalVisible: false,
    selectedRowKeys: null
  });

  const {fbId, user} = props

  useEffect(() => {
    if (typeof window !== "undefined") {
      console.log("on browser");
      const { Column } = require("@antv/g2plot");

      const chart = new Column(document.getElementById("chart"), {
        data: chartData,
        xField: "name",
        yField: "value",
        padding: "auto",
        title: {
          visible: true,
          text: "Interest stats"
        },
        forceFit: true
      });
      console.log("el", document.getElementById("chart"));
      chart.render();
    }
  }, []);

  useEffect( () => {
    fetchAdAccounts()
  }, [state.modalVisible])

  const setModalVisible = modalVisible => {
    setState({
      ...state,
      modalVisible
    });

  };

  const fetchAdAccounts = async () => {
    if(state.modalVisible){
      const adAccounts = await getAdAccounts(fbId, user.xToken)
      console.log(adAccounts.data)
      setState({
        ...state,
        adAccounts: adAccounts
      })
    }
  }

  const onSelectionChange = (selectedRowKeys, selectedRows) => {
    console.log("rows", selectedRows);
    const selectedInterest = selectedRows.map(interest => {
      return interest.name;
    });
    setState({
      ...state,
      selectedRowKeys,
      selectedInterest,
      selectedRows
    });
  };

  const { Option } = Select;
  const { selectedRowKeys } = state;
  return (
    <div id="performance">
      <Row gutter={16} style={{ width: "100%" }}>
        <Col span={4}>
          <div className="selection">
            <div className="button item">
              <Button onClick={()=>setModalVisible(true)} block>
                Select Ad account
              </Button>
            </div>
            <div className="select item">
              <Select style={{ width: "100%" }} placeholder="Select campaign">
                <Option value="jack">Jack</Option>
                <Option value="lucy">Lucy</Option>
                <Option value="disabled" disabled>
                  Disabled
                </Option>
                <Option value="Yiminghe">yiminghe</Option>
              </Select>
            </div>
            <div className="select item">
              <Select style={{ width: "100%" }} placeholder="Select adset">
                <Option value="jack">Jack</Option>
                <Option value="lucy">Lucy</Option>
                <Option value="disabled" disabled>
                  Disabled
                </Option>
                <Option value="Yiminghe">yiminghe</Option>
              </Select>
            </div>
            <div className="select item">
              <Select style={{ width: "100%" }} placeholder="Select ad">
                <Option value="jack">Jack</Option>
                <Option value="lucy">Lucy</Option>
                <Option value="disabled" disabled>
                  Disabled
                </Option>
                <Option value="Yiminghe">yiminghe</Option>
              </Select>
            </div>
          </div>
        </Col>
        <Col span={20}>
          <div className="stats">
            <div id="chart"></div>
            <div>
              <Table
                columns={performanceColumns}
                onSelection={onSelectionChange}
                selectedRowKeys={selectedRowKeys}
                className="table"
              />
            </div>
          </div>
        </Col>
      </Row>
      <Modal
        title="Vertically centered modal dialog"
        centered
        visible={state.modalVisible}
        onOk={() => setModalVisible(false)}
        onCancel={() => setModalVisible(false)}
      >
        {state.adAccounts? state.adAccounts.data.map((item) => {
          <AdAccount name="ben"/>
        }): "LOADING..."}
      </Modal>
      <style jsx>
        {`
          #performance {
            display: flex;
            position: relative;
            margin-top: 70px;
            width: 100%;
          }

          .selection {
            display: flex;
            flex-direction: column;
            border: 1px solid #ccc;
            border-radius: 4px;
            height: auto;
            padding: 10px;
          }

          .item {
            margin-bottom: 30px;
            width: 100%;
          }
          .stats {
            display: flex;
            flex-direction: column;
          }
        `}
      </style>
    </div>
  );
}

const mapStateToProps = state => ({
  user: state.user.currentUser,
  fbId: state.user.fbToken.fbId
});

export default connect(mapStateToProps)(Performance);
