const Plan = (props) => {
  return (
    <div className="wrapper">
      <div className="header">
        <div className="plan-image">
          <img src={props.image} />
        </div>
        <h3>{props.title}</h3>
        <p>{props.description}</p>
      </div>
      <div className="features">
        {props.features.map((feature) => (
          <div className="feature">
            {" "}
            <div className="feature-image">
              <img src={feature.image} />
            </div>
            <p>{feature.description}</p>
          </div>
        ))}
      </div>
      <style jsx>{`
        .wrapper {
          display: flex;
          flex-direction: column;
          border: 1px solid #eee;
          padding: 15px;
          width: 40%;
        }

        .header {
          display: flex;
          flex-direction: column;
          align-items: center;
        }

        .plan-image {
          display: flex;
          justify-content: center;
          width: 100%;
          margin-bottom: 10px;
        }

        .features {
          margin-top: 50px;
        }

        .feature {
          display: flex;
          align-items: center;
          justify-content: center;
          margin-bottom: 20px;
        }

        .feature-image {
          display: flex;
          align-items: center;
          justify-content: center;
          min-width: 100px;
          margin-right: 10px;
        }

        .feature-image img {
          width: 100%;
        }

        .plan-image img {
          width: 10%;
        }
      `}</style>
    </div>
  );
};

export default Plan;
