import {useState} from 'react'

import { Button, Input } from "antd";

function Signin() {
  const [state, setState] = useState({
    current: 'signup',
    email:'',
    password: '',
    error: {
      email: null,
      password: null
    }
  })

  const setInput = (event) => {
    console.log(event.target.name)
    const {name, value} = event.target
    setState((prevState) => ({
      ...prevState,
      [name]: value
    }))
  }

  const handleSubmit = () => {

    if(!validateEmail(state.email)){
      setState((prevState) => ({
        ...prevState,
        error: {
          ...prevState.error,
          email: 'Email is wrong'
        }
      }))
    }

    if(!state.password.length > 7){
      console.log('wrong pass')
      setState((prevState) => ({
        ...prevState,
        error: {
          ...prevstate.error,
          password: 'Password must be longer than 7 characters'
        }
      }))
    }

  }

  function validateEmail(email) {
    var re = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
    return re.test(String(email).toLowerCase());
}

  const {current} = state
  return (
    <div id="wrapper">
      <div className="pane">
        <img src="/images/newLogoWhite.svg"/>
        <div className="slogan">Hyper target your Facebook ads</div>
        </div>
      <div className="auth">
        <div className="details">
          <h3>{current === "signup"? "Sign up" : "Sign in"}</h3>
          <div className="input">
            <p>{state.error.email ? state.error.email: ''}</p>
            <Input 
              name="email"
              type="email"
              placeholder="email" 
              onChange={setInput}
            />
          </div>
          <div className="input">
          <p>{state.error.password ? state.error.password: ''}</p>
          <Input 
              name="password"
              type="password"
              placeholder="password" 
              onChange={setInput}
            />
          </div>
          <Button type='primary' style={{width: '100%'}} onClick={handleSubmit}>
            {current === "signup"? "Sign up" : "Sign in"}
          </Button>
          <div id="sub-actions">
            <p>By signing up, you agree to our terms and conditions</p>
            <p>Have an acount already? <span>{current === "signup"? "Sign up" : "Sign in"}</span></p>
          </div>
        </div>
      </div>
      <style jsx>{`
        #wrapper {
          display: flex;
          width: 100%;
          height: 100vh;
        }

        .pane {
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
          background-color: #4e54c8;
          flex: 1;
        }

        .auth {
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
          flex: 2;
        }

        .details {
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
          width: 40%;
        }

        .input {
          width: 100%;
          margin: 10px 0 10px 0;
        }

        .card {
          color: black;
          display: flex;
          border-radius: 4px;
          width: 30%;
          background-color: white;
          height: 100px;
        }

        .slogan {
          color:white;
          font-size:18px;
        }

        #sub-actions{
          display: flex;
          flex-direction: column;
          justify-content: center;  
          text-align: center;
          margin-top: 10px;
        }

        #sub-actions p {
          margin: 0;
          margin-bottom: 5px; 
        }

        #sub-actions span {
          color: #4e54c8;
          cursor: pointer;
          font-weight: bold;
        }
      `}</style>
    </div>
  );
}

Signin.Layout = (props) => <div>{props.children}</div>;

export default Signin;
