import { takeLatest, put, all, call } from "redux-saga/effects";
import UserActionTypes from "./user-types";
import {
  siginSuccess,
  setCurrentUser,
  siginFailure,
  isLoading
} from "./user-actions";

export function* setUser({ payload }) {
  console.log("********", payload);
  // try {
  //   yield put(isLoading(true));
  //   if (!payload) {
  //     throw new Error("No user");
  //   }
  //   yield console.log("setuser**********", payload);

  //   yield put(setCurrentUser(payload));
  //   yield put(siginSuccess(true));
  //   yield put(isLoading(false));
  // } catch (error) {
  //   yield put(siginFailure(error));
  //   yield put(isLoading(false));
  // }
}

export function* signIn() {}

export function* onAddingCurrentUser() {
  yield takeLatest(UserActionTypes.ADDING_CURRENT_USER, setUser);
}

export function* onSigninStart() {
  yield takeLatest(UserActionTypes.SIGN_IN_START, signIn);
}

export function* userSagas() {
  yield all([call(onAddingCurrentUser), call(onSigninStart)]);
}
