import { Row, Col } from 'antd';

const DashboardLayout = props => {
 return (
     <div className="layout">
         <Row>
            <Col style={{position: 'relative', height: '100vh'}} span={4}>{props.sidebar}</Col>
            <Col style={{padding: '0 20px 0 20px', minHeight: '100vh'}} span={20} className="child-container">
            <div className="children">
                {props.children}
            </div>

            <footer>
                <div className="footer">
                    <p className="footer-item">&copy; 2010 - {new Date().getFullYear()}, Getch.</p>
                </div>
            </footer>
            </Col>
         </Row>

        <style jsx>{`
            .layout {
                position: relative;
                min-height: 100vh;
            }

            .layout-body {
                position: relative;
            }

            .child-container {
                display: flex;
            }

            footer {
                margin-top: 100px;
                display: flex;
                justify-content: center;
                width: 100%;
                background: white;
                height: 50px;
                margin-top: 50px;
            }

            .footer {
                position: fixed;
                bottom: 0;
                width: inherit;
                background-color: white;
                color: black;
                text-align: center;
              }

        `}</style>
     </div>
 )
}

export default DashboardLayout
