import FacebookLogin from "react-facebook-login/dist/facebook-login-render-props";
import { Modal, Button, Select, Row, Col } from "antd";
import { connect } from "react-redux";

import { authFacebook } from "../redux/user/user-actions";

const Onboarding = (props) => {
    const {
        authFb,
        user,
        fbToken
      } = props;

    const faceAuthStart = () => {
        console.log("#### FB auth start");
    };

    const responseFacebook = async res => {
        const { accessToken, userID } = res;
        console.log('fb',res)
        authFb(accessToken, user.id, userID);
    };
  return (
    <div>
      <Modal visible={props.fbId} centered footer={null} closable={false}>
        <div className="modal-inner">
        <div className="fb"><img src="/images/facebook.svg"/></div>
          <h3>Lets search Facebook</h3>
          <p>Connect to Facebook to begin searching for interest</p>
          <FacebookLogin
          appId={process.env.FB_APP_ID}
          autoLoad={true}
          fields="name,email,id"
          onClick={faceAuthStart}
          scope="ads_management, email"
          callback={responseFacebook}
          render={renderProps => (
            <Button
              disabled={!!fbToken}
              type="primary"
              onClick={renderProps.onClick}
            >
              Connect Facebook
            </Button>
          )}
        />
        </div>
      </Modal>
      <style jsx>{`
        .modal-inner {
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
        }

        .fb{
            width: 50px;
        }

        .fb img {
            width: 100%;
        }
      `}</style>
    </div>
  );
};

const mapStateToProps = (state) => ({
  fbId: state.user.fbToken.fbId,
  fbToken: state.user.fbToken
});

const mapDispatchToProps = dispatch => ({
    authFb: (token, id, fbId) => dispatch(authFacebook({ token, id, fbId}))
  });

export default connect(mapStateToProps, mapDispatchToProps)(Onboarding);
