import { Row, Col } from 'antd';

const DashboardLayout = props => {
 return (
     <div className="layout">
         <Row>
            <Col style={{position: 'relative', height: '100vh'}} span={4}>{props.sidebar}</Col>
            <Col style={{padding: '0 20px 0 20px'}} span={20}>
                {props.children}
            <footer>
                <div className="footer">
                <p className="footer-item">&copy; 2010 - {new Date().getFullYear()}, Getch.</p>
                </div>
            </footer>
            </Col>
         </Row>

        <style jsx>{`
            .layout {
                position: relative;
                min-height: 100vh;
            }

            .layout-body {
                position: relative;
            }

            footer {
                display:flex;
                justify-content: center;
                margin-bottom: 20px;
                margin-top:20px;
                background: red;
              }

              .footer {
                position: absolute;
                width: inherit;
                z-index: 20;
              }
        `}</style>
     </div>
 )
}

export default DashboardLayout
